var e={id:"operation-pdf-builder",name:"PDF Builder Operation",icon:"picture_as_pdf",description:"Generate a pdf with flow data and the template.",overview:({filename:e,fonts:i})=>[{label:"Filename",text:e},{label:"Fonts",text:t(i)}],options:[{field:"permissions",name:"$t:permissions",type:"string",schema:{default_value:"$trigger"},meta:{width:"full",interface:"select-dropdown",options:{choices:[{text:"From Trigger",value:"$trigger"},{text:"Public Role",value:"$public"},{text:"Full Access",value:"$full"}],allowOther:!0}}},{field:"filename",name:"$t:fields.directus_files.filename_download",type:"string",meta:{width:"full",interface:"input"}},{field:"folder",name:"$t:folder",type:"uuid",meta:{width:"half",interface:"system-folder"}},{field:"storage",name:"$t:fields.directus_files.storage",type:"string",meta:{width:"half",interface:"input",options:{placeholder:"local",iconRight:"storage"}}},{field:"template",name:"$t:template",type:"json",meta:{width:"full",interface:"input-code",options:{lineWrapping:!0,language:"JSON",template:JSON.stringify({content:[]})}}},{field:"fonts",name:"Fonts",type:"json",meta:{width:"full",interface:"list",special:"cast-json",options:{template:"{{ font_family }} - {{ font_type }}",fields:[{field:"font_family",name:"Font Family",type:"string",meta:{width:"full",interface:"input",required:!0,options:{placeholder:"Roboto",iconRight:"type_specimen"}}},{field:"font_type",name:"Font Type",type:"string",meta:{width:"full",interface:"select-dropdown",required:!0,options:{allowOther:!0,choices:[{text:"Regular",value:"Regular"},{text:"Bold",value:"Bold"},{text:"Italic",value:"Italic"},{text:"BoldItalic",value:"BoldItalic"}]}}},{field:"url",name:"Url",type:"string",meta:{width:"full",interface:"input",required:!0,options:{placeholder:"https://example.com/fonts/font.ttf",iconRight:"link"}}}]}}},{field:"images",name:"Images",type:"json",meta:{width:"full",interface:"list",special:"cast-json",options:{template:"{{ image_name }}",fields:[{field:"image_name",name:"Name",type:"string",meta:{width:"full",interface:"input",required:!0,options:{placeholder:"image",iconRight:"image"}}},{field:"url",name:"Url",type:"string",meta:{width:"full",interface:"input",required:!0,options:{placeholder:"https://example.com/image.jpg",iconRight:"link"}}}]}}}]};function t(e){if(Array.isArray(e)){const t=e.reduce(((e,t)=>e.find((e=>e.font_family===t.font_family))?e:e.concat([t])),[]);return t.map((e=>`${e.font_family} - ${e.font_type}`)).join(t.length>1?", ":"")}return"$t:no_items"}export{e as default};
